from rlc import RLCfrom photon import Photonfrom matplotlib import pyplot as pltimport numpy as npm=1 # iterationdt = 1e-8 #time stepn = 1 #steps per time stepI = 1 #intensity in photons per time stepT = 750 #effective temperature of light sourcer = 100e-6l=100e-6c=1e-9a = 500e-9t=[0,1e-6]ic=[1,0]sys = RLC(r,l,c,t,ic)systemp = sysfor step in np.arange(t[0],t[1]-dt,dt):    p = Photon(T, I)    omega = systemp.omega    wshift= np.sum(p.random_frequency()*p.coupler(a,a/3))        print(wshift)        systemp.omega = omega+wshift    systemp.tint = [step, step+dt]        t, x, v = systemp.solve_ivp(n)        plt.plot(t,x)    systemp.ic=[x[-1],v[-1]]    plt.show()        